INSERT INTO `news_info` VALUES ('2020-04-11 06:02:00', '捷克新增新冠肺炎确诊病例207例 累计确诊5674例', '根据捷克卫生部公布的数据，从当地时间4月9日下午17:30至4月10日下午17:30，境内新增新冠肺炎确诊病例207例，累计确诊5674例', '新华社');
