INSERT INTO `news_info` VALUES ('2020-04-02 09:52:00', '江西4月1日无新增新冠肺炎确诊病例', '2020年4月1日0-24时，江西省无本地新增新型冠状病毒肺炎确诊病例报告，无本地新增疑似病例，无本地住院确诊病例。', '央视网');
