INSERT INTO `news_info` VALUES ('2020-03-31 10:20:00', '新西兰总理穿居家卫衣与网友聊新冠封锁令', '为抗击新型冠状病毒，新西兰政府要求公民在未来几周内留守家中。禁令生效后，新西兰总理阿德恩在家中穿着卫衣与民众视频聊天，告之他们当天发生的事情及一些注意事项。目前新西兰共有589例确诊病例，1例死亡。\n', '人民日报');
