INSERT INTO `news_info` VALUES ('2020-02-09 16:18:00', '山东出现首例死亡病例', '据山东省卫健委网站9日消息，2月8日12至24时，山东省报告新型冠状病毒感染的肺炎死亡病例1例，这也是山东省首例。据悉，患者李某某，女，85岁，既往有糖尿病、高血压、冠心病、心功能衰竭、子宫肌瘤等病史。', '人民日报');
